let a1, a2, a3, a4, a5: int; fn me(x: int) -> int { return x;
} fn add(x: double, y: double) -> int { let xx: int = x as int; let yy: int = y as int; return xx + yy
} fn give(idx: int) -> int { if idx == 1 { return a1; } else if idx == 2 { return a2; } else if idx == 3 { return a3; } else if idx == 4 { return a4; } else if idx == 5 { return a5; } else { return 114514.0 as int }
} fn set(idx: int, val: int) -> void { if idx == 1 { a1 = val; } else if idx == 2 { a2 = val; } else if idx == 3 { a3 = val; } else if idx == 4 { a4 = val; } else if idx == 5 { a5 = val; }
} fn main() -> void { let a, b, c, t: int; let five: int = 5; a = getint(); b = getint(); set(1, a); set(2, b); t = 3; while t <= five { c = add(a as double, b as double); b = a; a = c; set(t, c); t = t + 1; } print(give(me(five)));

